transforms.result:
@use './colors' as c;

.offers {
  &__subtitle {
    min-width: fit-content;
    width: 90%;
    margin-top: 25px;
    padding: 1.15rem;
    font-size: 1.1rem;
    font-weight: 100;
    text-align: center;
  }

  &__content {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  // OFFERS SUBPAGE

  &__selector {
    display: flex;

    &-btns {
      margin: 0 auto;
      padding: 2rem;
      &--btn {
        margin: 0.55rem;
      }
    }
  }

  &__boxes {
    display: flex;
    flex-direction: column;
    margin-bottom: 1.5rem;
  }

  &__box {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 80%;
    margin: 2rem auto;

    &:not(:last-child) {
      border-bottom: 1px solid c.$secondary-color;
    }

    &-details {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      height: 100%;
      &--btn {
        margin: 1rem auto 2rem;
        font-size: 1.5rem;
        font-family: var(--bold-font);
      }

      &--description {
        padding: 2rem;
        line-height: 1.8rem;
      }
    }

    &-item {
      position: relative;
      &--img {
        width: 16.5rem;
        height: 14.5rem;
        margin: 1rem 1rem 2rem;
        border-radius: 5%;
        cursor: pointer;
      }
      &--more {
        position: absolute;
        font-size: 2rem;
        bottom: 50px;
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 0.75rem;
        // border-radius: 50%;
        background-color: rgba(0, 0, 0, 0.65);
        height: 55px;
        width: 50px;
        right: 25px;
      }
    }
  }

  &__advanced {
    width: 100%;

    &--title {
      margin-bottom: 1rem;
      padding: 1rem;
      font-size: 2rem;
    }

    &--link {
      margin-top: 1rem;
      padding: 1rem;
      font-size: 1.15rem;
    }
  }

  // OFFERS SUBPAGE

  &__card {
    margin: 2rem auto;
    padding: 2rem;
    border: 1px solid c.$parallax-border-color;
    border-radius: 0.5rem;
    -webkit-box-shadow: 1px 2px 16px -7px rgba(66, 68, 90, 1);
    -moz-box-shadow: 1px 2px 16px -7px rgba(66, 68, 90, 1);
    box-shadow: 1px 2px 16px -7px rgba(66, 68, 90, 1);

    &:nth-child(3) {
      padding: 2rem 2.73rem;
    }

    &-infoBox {
      margin-top: -1rem;
      padding: 0.5rem;

      &--title {
        font-size: 1.5rem;
        margin: 0.5rem auto;
      }
      &--price {
        margin-bottom: 1rem;
        font-family: var(--bold-font);
        font-size: 2.45rem;
      }
    }

    &-option {
      display: flex;
      margin: 0.25rem auto;
      padding: 0.05rem 1rem;
      font-size: 0.95rem;

      &--image {
        margin-right: 0.5rem;
      }
    }
    &--button {
      margin-top: 1.5rem;
    }
  }
  &__button {
    padding: 1rem;
    &--btn {
      width: 100%;
      padding: 1rem;
      text-transform: uppercase;
    }
  }
}

