transforms.result:
@use './colors' as c;

.offers {
  // min-width: 10rem;
  &__subtitle {
    font-weight: 100;

    width: 90%;
    text-align: center;
    padding: 1.15rem;
    font-size: 1.1rem;
    min-width: fit-content;
    margin-top: 25px;
  }

  &__content {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  // OFFERS SUBPAGE

  &__selector {
    display: flex;

    &-btns {
      margin: 0 auto;
      padding: 2rem;
      &--btn {
        margin: 0.3rem;
      }
    }
  }

  &__boxes {
    display: flex;
    flex-direction: column;
  }

  &__box {
    display: flex;
    align-items: center;
    flex-direction: column;
    margin: 2rem auto;
    border: 1px solid c.$secondary-color;
    border-radius: 0.05rem;
    width: 80%;

    &:nth-child(2) {
      flex-direction: column-reverse;
    }
    &-details {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      // background-color: rgb(154, 197, 161);
      height: 100%;
      &--btn {
        margin: 1rem auto 2rem;
        font-size: 1.5rem;
        font-family: var(--bold-font);
      }

      &--description {
        padding: 2rem;
        line-height: 1.5rem;
      }
    }

    &--img {
      width: 100%;
    }
  }

  &__advanced {
    width: 100%;

    &--title {
      font-size: 1.7rem;
    }
  }

  // OFFERS SUBPAGE

  &__card {
    margin: 2rem auto;
    padding: 2rem;
    // padding: 1rem; >200vp
    border: 1px solid c.$parallax-border-color;
    // border: 1px solid black;
    border-radius: 0.5rem;
    -webkit-box-shadow: 1px 2px 16px -7px rgba(66, 68, 90, 1);
    -moz-box-shadow: 1px 2px 16px -7px rgba(66, 68, 90, 1);
    box-shadow: 1px 2px 16px -7px rgba(66, 68, 90, 1);

    &:nth-child(3) {
      padding: 2rem 2.73rem;
    }

    &-infoBox {
      margin-top: -1rem;
      padding: 0.5rem;

      &--title {
        font-size: 1.5rem;
        margin: 0.5rem auto;
      }
      &--price {
        font-family: var(--bold-font);
        font-size: 2.45rem;
        margin-bottom: 1rem;
      }
    }

    &-option {
      display: flex;
      margin: 0.25rem auto;
      // border: 1px solid;
      padding: 0.05rem 1rem;
      font-size: 0.95rem;

      &--image {
        margin-right: 0.5rem;
      }
    }
    &--button {
      margin-top: 1.5rem;
    }
  }
}

.offers__subtitle {
  font-weight: 100;
  width: 90%;
  text-align: center;
  padding: 1.15rem;
  font-size: 1.1rem;
  min-width: fit-content;
  margin-top: 25px;
}
.offers__content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.offers__selector {
  display: flex;
}
.offers__selector-btns {
  margin: 0 auto;
  padding: 2rem;
}
.offers__selector-btns--btn {
  margin: 0.3rem;
}
.offers__boxes {
  display: flex;
  flex-direction: column;
}
.offers__box {
  display: flex;
  align-items: center;
  flex-direction: column;
  margin: 2rem auto;
  border: 1px solid #000000;
  border-radius: 0.05rem;
  width: 80%;
}
.offers__box:nth-child(2) {
  flex-direction: column-reverse;
}
.offers__box-details {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
}
.offers__box-details--btn {
  margin: 1rem auto 2rem;
  font-size: 1.5rem;
  font-family: var(--bold-font);
}
.offers__box-details--description {
  padding: 2rem;
  line-height: 1.5rem;
}
.offers__box--img {
  width: 100%;
}
.offers__advanced {
  width: 100%;
}
.offers__advanced--title {
  font-size: 1.7rem;
}
.offers__card {
  margin: 2rem auto;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.7);
  border-radius: 0.5rem;
  -webkit-box-shadow: 1px 2px 16px -7px rgb(66, 68, 90);
  -moz-box-shadow: 1px 2px 16px -7px rgb(66, 68, 90);
  box-shadow: 1px 2px 16px -7px rgb(66, 68, 90);
}
.offers__card:nth-child(3) {
  padding: 2rem 2.73rem;
}
.offers__card-infoBox {
  margin-top: -1rem;
  padding: 0.5rem;
}
.offers__card-infoBox--title {
  font-size: 1.5rem;
  margin: 0.5rem auto;
}
.offers__card-infoBox--price {
  font-family: var(--bold-font);
  font-size: 2.45rem;
  margin-bottom: 1rem;
}
.offers__card-option {
  display: flex;
  margin: 0.25rem auto;
  padding: 0.05rem 1rem;
  font-size: 0.95rem;
}
.offers__card-option--image {
  margin-right: 0.5rem;
}
.offers__card--button {
  margin-top: 1.5rem;
}
