  }
}

@media (width >=576px) {
  .offers {
    &__box {
      &-item {
        &--img {
          width: 25.5rem;
        }
      }
    }
  }
}

@media (width >= 768px) {
  .header {
    &__box {
      &--title {
        font-size: 1.75rem;
      }

      &--subtitle {
        font-size: 2.5rem;
      }
    }
  }

  .about {
    &__box {
      &--image {
        width: 433px;
        height: 333px;
      }
    }
  }
}

@media (width >= 850px) {
  .carousel {
    min-width: 47.688rem;
  }
}

@media (width >= 992px) {
  .noScroll {
    overflow: auto;
  }

  .sectionTitle {
    margin-bottom: 1.25rem;
  }

  .nav {
    &__burger {
      display: none;
    }
    &__mobile {
      display: none;
    }
    &__desktop {
      display: flex;
    }
  }

  .header {
    &__hero {
      background-image: url('../img/heroHD.jpg');
    }
  }

  .about {
    &__content {
      flex-direction: row;
    }

    &__box {
      padding: 1.25rem;
      align-items: center;
      justify-content: center;

      &--title {
        width: 110%;
        text-align: left;
      }

      &-text {
        width: 110%;
        padding: 0.25rem;
        line-height: 2.05rem;
      }

      &:nth-child(2) {
        display: flex;
        text-align: left;
        justify-content: flex-start;
      }
    }
  }

  .parallax {
    &__hero {
      background-attachment: fixed;
      &-box {
        width: 70%;
        &--title {
          font-size: 2.5rem;
        }
      }
    }
  }

  .offers {
    &__content {
      flex-direction: row;
    }
    &__card {
      margin: 2rem 1rem;
    }

    // subpage

    &__boxes {
      flex-direction: row;
      flex-wrap: wrap;
    }
    &__box {
      flex-direction: row;

      &:nth-child(2) {
        flex-direction: row-reverse;
      }
      &-details {
        flex-direction: column;
      }

      &-item {
        &:hover &--img {
          transition: scale 0.3s ease-in-out, opacity 0.3s ease-in-out;
          opacity: 1;
          scale: 1.03;
        }
        &--img {
          width: 27rem;
          height: 20rem;
          transition: scale 0.3s ease-in-out, opacity 0.3s ease-in-out;
          opacity: 0.5;
        }
      }
    }

    // subpage
  }

  .carousel {
    min-width: 55rem;
  }

  .error {
    &__content {
      flex-direction: row-reverse;
    }
    &__box {
      &--title {
        position: absolute;
        top: 50px;
        width: 100%;
        margin-top: -2.5rem;
        font-size: 2.35rem;
      }
      &--image {
        width: 35rem;
        margin: 0 1rem;
      }
      &-button {
        position: absolute;
        bottom: -10px;
      }
    }
  }

  .footer {
    &__container {
      flex-direction: row;
      margin: 0 auto;
      width: 57.5rem;
    }
    &__logo {
      margin-top: -4rem;
      margin-right: 2.5rem;
    }
    &__list {
      height: 14rem;
      &--title {
        width: 10rem;
        margin-left: 1.906rem;
      }
      &--item {
        justify-content: flex-start;
        width: 9rem;
      }

      &:nth-child(3) {
        margin-left: -2rem;
      }

      &:nth-child(4) {
        margin-left: 0.5rem;
      }
    }
  }

  .contact {
    &__brand {
      justify-content: center;
    }

    &__info {
      margin: 0 auto;

      &-link {
        margin: 0;
      }
    }

    &__form {
      padding: 1.5rem;

      &-box {
        &--checkbox {
          margin: 0 0.5rem;
        }
      }
      &-option {
        &:nth-child(6) {
          flex-direction: row;
        }
      }
      &-btns {
        flex-direction: row;

        &--btn {
          margin: 0 0.5rem;
        }
      }
    }

    &__boxes {
      flex-direction: row;
    }

    &__box {
      flex-direction: row;
      margin: 0.75rem;
    }

    &__mapBox {
      margin-top: 0;
      padding: 0;
      height: 40rem;
      &--map {
        width: 21.5rem;
        height: 44.25rem;
      }
    }
  }
}

@media (width >= 1200px) {
  .about {
    &__box {
      &--image {
        width: 503px;
        height: 333px;
      }
      &:nth-child(2) {
        padding: 2.25rem;
        margin-top: -2.5rem;
      }
      &-text {
        padding: 0.25rem;
        width: 110%;
      }
    }
  }

  .carousel {
    min-width: 70rem;
    height: 60%;
  }

  .error {
    &__box {
      &--image {
        width: 45rem;
      }
    }
  }
}

@media (width >= 1400px) {
  .header {
    &__box {
      top: 37%;
      left: 64%;
      align-items: flex-start;
      &--subtitle {
        text-align: left;
      }
    }
  }
}

